package mdc.voodoocraft.hexes;import net.minecraft.entity.EntityAgeable;import net.minecraft.entity.EntityLivingBase;import net.minecraft.entity.monster.EntityZombie;import net.minecraft.entity.player.EntityPlayer;import net.minecraft.item.ItemStack;import net.minecraft.util.EnumHand;import net.minecraft.util.math.BlockPos;import net.minecraft.world.World;import javax.annotation.Nullable;public class HexFertility extends HexEntry{    public HexFertility()    {        super("fertility");    }    @Override    public ItemStack activeUse(ItemStack stackIn, World world, EntityPlayer player, EnumHand hand, int strength, @Nullable EntityLivingBase target)    {        if(world.isRemote || target == null) return super.activeUse(stackIn, world, player, hand, strength, target);        if(target instanceof EntityZombie && !target.isChild())        {            EntityZombie entity = new EntityZombie(world);            entity.setChild(true);            entity.copyLocationAndAnglesFrom(target);            entity.onInitialSpawn(world.getDifficultyForLocation(new BlockPos(entity)), null);            world.spawnEntity(entity);        }        else if(target instanceof EntityAgeable && !target.isChild())        {            EntityAgeable entity = (EntityAgeable) target;            EntityAgeable child = entity.createChild(entity);            child.copyLocationAndAnglesFrom(entity);            child.setGrowingAge(-24000);            child.onInitialSpawn(world.getDifficultyForLocation(new BlockPos(entity)), null);            world.spawnEntity(child);        }        return super.activeUse(stackIn, world, player, hand, strength, target);    }}